import React from "react";
import "./style.css";
import { useHistory } from "react-router-dom";
import { useParams } from "react-router-dom";

const StarPage = (props) => {
  const history = useHistory();
  const weeks_name = useParams();

  let c = ["1", "2", "3", "4", "5"];

  // const star = React.useRef(null);
  // console.log(star)

  // const hoverEvent = () => {
  //   star.current.style.background = "yellow";
  // };

  // React.useEffect(() => {
  //   star.current.addEventListener("click", hoverEvent);
  //   return () => {
  //     star.current.removeEventlistener("click", hoverEvent);
  //   };
  // }, []);

  const [count, setCount] = React.useState();

  return (
    <div>

      <h3>
        <span className="day">{weeks_name.week_name}요일</span> 평점 남기기
      </h3>

      <div className="circle-list">
        {c.map((a, i) => {
          return <div onClick={() => {
            setCount(i) 
            console.log(count)
          }} style={{backgroundColor: (i < count+1) ? "yellow":"lightgray"}} key={i} className="star_circle"></div>;
        })}
      </div>

      <button
        className="btn" onClick={() => {history.goBack();}}>평점 남기기
      </button>

    </div>
  );
};

export default StarPage;
