//react패키지에서 리액트 가져온다.
import React from "react";
import "./style.css";
import {useHistory} from "react-router-dom";


const Weeks = (props) => {
    const history = useHistory();


  let day = ["일", "월", "화", "수", "목", "금", "토"];




  return (
    <div>
      {/* 자바스크리트 안에서 html태그를 리턴해준 상태 -> {}안에서 자바스크립트 문법 사용 */}
      {/* <div className="weeks-list">일 을 배열의 index 만큼 도는 함수*/}

      {day.map((e, i) => {

        let randomStar = Math.floor(Math.random() * 5)+1;

        return (
          <div key={i} className="weeks-list">
            {e}





            {Array.from({ length: 5 }, (v, i) => {
              return <div style={{backgroundColor: (i < randomStar) || (i === 4) ? "yellow":"lightgray"}} key={i} id="circle"></div>;

            })}

            {/* <div id="circle"></div> 을 길이만큼(다섯번) 도는 함수*/}

            <div id="arrow" onClick={() => {
                console.log(e)
                history.push("/StarPage/"+e);
              }}> 
            </div>

          </div>
        );
      })}
    </div>
  );
};

//만든 컴포넌트를 다른 파이에서도 불러다가 쓸 수 있도록 export해줌!
export default Weeks;
