//react패키지에서 리액트 가져온다.
import React from "react";
import "./style.css";
import {useHistory} from "react-router-dom";
import {useParams} from "react-router-dom";




const StarPage = (props) => {

    const history = useHistory();
    const weeks_name = useParams();
    console.log(weeks_name);

    // let c = ["1", "2", "3", "4", "5"];


    const star = React.useRef(null);
    console.log(star)

    const hoverEvent = () => {
        star.current.style.background = "yellow";
    }

    React.useEffect(() => {
        star.current.addEventListener("click", hoverEvent);

        return () => {
            star.current.removeEventlistener("click", hoverEvent);
        }
    }, []);


    return (
        <div>
            <h3><span className="day">{weeks_name.week_name}요일</span> 평점 남기기</h3>
            <div className="circle-list">
                {Array(5).fill(0).map((a,i)=>{

                    return (
                        <div ref={star} key={i} id="star_circle" ></div>
                    )
                })}
            </div>
            <button className="btn" onClick={() => {
                history.goBack()
            }}>평점 남기기</button>
        </div>
    );
}


//만든 컴포넌트를 다른 파이에서도 불러다가 쓸 수 있도록 export해줌!
export default StarPage;
